import React, { useState, useRef } from 'react';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';

export default function App() {
  const [barcodeForm, setBarcodeForm] = useState({
    productName: '',
    serialNumber: '',
    barcodeValue: '',
    lastUpdate: '',
    areaManager: '',
    supervisor: '',
    mobileNumber: '',
    location: '',
    fault: '',
    accepted: '',
    deliveredBy: '',
    remark: ''
  });
  const barcodeRef = useRef();
  // Scan input state and scanned barcode details
  const [scanValue, setScanValue] = useState("");
  const [scannedBarcode, setScannedBarcode] = useState(null);
  const [scanLoading, setScanLoading] = useState(false);
  const [scanError, setScanError] = useState("");

  // Fetch barcode details by scanned value
  const handleScan = async (e) => {
    const value = e.target.value.trim().toUpperCase();
    setScanValue(value);
    setScanError("");
    setScannedBarcode(null);
    if (value) {
      setScanLoading(true);
      try {
        const { api } = await import("./services/api");
        const res = await api.get(`/barcodes/scan?value=${value}`);
        console.log('API response:', res);
        if (res && !res.error) {
          setScannedBarcode(res);
        } else {
          setScanError("No barcode found.");
        }
      } catch (err) {
        // If error is due to invalid JSON (HTML response), show specific message
        if (err.message && err.message.includes('Unexpected token')) {
          setScanError("Error: Received invalid response from server. Backend may not be running or endpoint is incorrect.");
        } else {
          setScanError("Error: " + (err.message || "No barcode found."));
        }
      }
      setScanLoading(false);
    } else {
      setScannedBarcode(null);
      setScanLoading(false);
    }
  };

  // Automatically update barcodeValue when serialNumber changes
  const handleSerialChange = (e) => {
    const serial = e.target.value.toUpperCase();
    setBarcodeForm(form => ({
      ...form,
      serialNumber: serial,
      barcodeValue: serial // barcodeValue is always the serial number
    }));
  };

  // Use react-to-print for printing
  const handlePrint = useReactToPrint({
    content: () => barcodeRef.current
  });

  return (
    <React.Fragment>
      <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #eef2f7 0%, #e0e7ff 100%)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
        <nav style={{ background: 'white', borderRadius: '0 0 20px 20px', boxShadow: '0 2px 12px rgba(0,0,0,0.10)', padding: '24px 40px', display: 'flex', gap: '32px', alignItems: 'center', marginBottom: '32px', justifyContent: 'center' }}>
          <img src="https://tse1.mm.bing.net/th/id/OIP.6ISiR68fTJizRexWth0ABQAAAA?cb=ucfimg2ucfimg=1&rs=1&pid=ImgDetMain&o=7&rm=3" alt="Logo" style={{ height: '80px', marginRight: '32px', objectFit: 'contain', boxShadow: '0 2px 8px #e0e7ff', background: 'white', borderRadius: '20px', padding: '4px' }} />
          <span style={{ fontSize: '32px', fontWeight: '900', color: '#6366f1', letterSpacing: '2px', marginRight: 'auto' }}>IT Department Asset Management System</span>
          <div style={{ display: 'flex', gap: '16px', background: '#f3f4f6', borderRadius: '12px', padding: '8px 16px', boxShadow: '0 2px 8px #e0e7ff' }}>
            <button style={{ background: '#059669', color: 'white', border: 'none', borderRadius: '8px', padding: '12px 28px', fontWeight: '700', fontSize: '18px', cursor: 'pointer', boxShadow: '0 2px 8px #05966933', transition: 'background 0.2s, color 0.2s' }}>Create Barcode</button>
          </div>
          <img src="https://cdn-icons-png.flaticon.com/512/5264/5264565.png" alt="User Icon" style={{ width: '40px', height: '40px', borderRadius: '50%', marginLeft: '24px', boxShadow: '0 2px 8px #e0e7ff', objectFit: 'cover', cursor: 'pointer', position: 'relative' }} />
        </nav>
        {/* Scan Barcode Input */}
        <div style={{ width: '100vw', display: 'flex', justifyContent: 'center', marginTop: '16px' }}>
          <div style={{ background: 'white', borderRadius: '16px', boxShadow: '0 2px 12px rgba(0,0,0,0.10)', padding: '24px 32px', maxWidth: '500px', width: '100%' }}>
            <h2 style={{ fontSize: '22px', fontWeight: '700', color: '#2563eb', marginBottom: '12px', textAlign: 'center' }}>Scan Barcode</h2>
            <input
              type="text"
              placeholder="Scan or enter barcode value"
              value={scanValue}
              onChange={handleScan}
              style={{ width: '100%', padding: '12px', fontSize: '17px', borderRadius: '8px', border: '1px solid #ddd', marginBottom: '16px' }}
            />
            {scanLoading && <div className="text-gray-500">Loading...</div>}
            {scanError && !scannedBarcode && <div className="text-red-500">{scanError}</div>}
            {scannedBarcode && (
              <div className="border p-6 rounded-lg bg-white shadow mt-4">
                <h3 className="font-bold text-lg mb-4 text-blue-700 border-b pb-2">Barcode Details</h3>
                <div className="grid grid-cols-2 gap-x-8 gap-y-2">
                  <div><span className="font-semibold text-gray-700">ID:</span> <span className="text-gray-900">{scannedBarcode._id}</span></div>
                  <div><span className="font-semibold text-gray-700">Product Name:</span> <span className="text-gray-900">{scannedBarcode.productName}</span></div>
                  <div><span className="font-semibold text-gray-700">Serial Number:</span> <span className="text-gray-900">{scannedBarcode.serialNumber}</span></div>
                  <div><span className="font-semibold text-gray-700">Barcode Value:</span> <span className="text-gray-900">{scannedBarcode.barcodeValue}</span></div>
                  <div><span className="font-semibold text-gray-700">Barcode Type:</span> <span className="text-gray-900">{scannedBarcode.barcodeType}</span></div>
                  <div><span className="font-semibold text-gray-700">Last Update:</span> <span className="text-gray-900">{scannedBarcode.lastUpdate}</span></div>
                  <div><span className="font-semibold text-gray-700">Remark:</span> <span className="text-gray-900">{scannedBarcode.remark}</span></div>
                  <div><span className="font-semibold text-gray-700">Area Manager:</span> <span className="text-gray-900">{scannedBarcode.areaManager}</span></div>
                  <div><span className="font-semibold text-gray-700">Supervisor:</span> <span className="text-gray-900">{scannedBarcode.supervisor}</span></div>
                  <div><span className="font-semibold text-gray-700">Mobile Number:</span> <span className="text-gray-900">{scannedBarcode.mobileNumber}</span></div>
                  <div><span className="font-semibold text-gray-700">Location:</span> <span className="text-gray-900">{scannedBarcode.location}</span></div>
                  <div><span className="font-semibold text-gray-700">Fault:</span> <span className="text-gray-900">{scannedBarcode.fault}</span></div>
                  <div><span className="font-semibold text-gray-700">Accepted:</span> <span className="text-gray-900">{scannedBarcode.accepted}</span></div>
                  <div><span className="font-semibold text-gray-700">Delivered By:</span> <span className="text-gray-900">{scannedBarcode.deliveredBy}</span></div>
                  <div><span className="font-semibold text-gray-700">Created At:</span> <span className="text-gray-900">{new Date(scannedBarcode.createdAt).toLocaleString()}</span></div>
                  <div><span className="font-semibold text-gray-700">Updated At:</span> <span className="text-gray-900">{new Date(scannedBarcode.updatedAt).toLocaleString()}</span></div>
                  <div><span className="font-semibold text-gray-700">Version:</span> <span className="text-gray-900">{scannedBarcode.__v}</span></div>
                </div>
              </div>
            )}
          </div>
        </div>
        <div style={{ width: '100vw', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #e0e7ff 0%, #f3f4f6 100%)' }}>
          <div style={{ background: 'white', borderRadius: '24px', boxShadow: '0 8px 32px rgba(0,0,0,0.10)', padding: '48px 40px', maxWidth: '900px', width: '100%', display: 'flex', flexDirection: 'column', gap: '40px', alignItems: 'center', justifyContent: 'center' }}>
            <form style={{ background: 'white', borderRadius: '16px', padding: '0', boxShadow: 'none', maxWidth: '600px', width: '100%' }}>
              {/* ...existing code for form... */}
              {/* All form fields and buttons remain unchanged */}
              {/* ...existing code for form... */}
            </form>
            <div ref={barcodeRef} style={{ background: '#f9fafb', borderRadius: '16px', padding: '32px', boxShadow: '0 4px 15px rgba(0,0,0,0.08)', maxWidth: '400px', width: '100%', minHeight: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
              <div style={{ display: 'flex', flexDirection: 'column', justifyContent: 'flex-end', height: '100%', width: '100%' }}>
                <div style={{ flex: 1 }}></div>
                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '40px' }}>
                  <PrintableBarcode barcode={barcodeForm} />
                </div>
              </div>
            </div>
            <div style={{ marginTop: '16px', fontSize: '15px', color: '#2563eb', fontWeight: 'bold', textAlign: 'center' }}>
              Produced by Tasma IT Dept.
            </div>
          </div>
        </div>
      </div>
    </React.Fragment>
  );
}
