import React, { useState } from 'react';

export default function App() {
  const [activeView, setActiveView] = useState('home');

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <nav style={{ background: 'white', borderRadius: '0 0 16px 16px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', padding: '16px 32px', display: 'flex', gap: '24px', alignItems: 'center' }}>
        <button onClick={() => setActiveView('home')} style={{ background: activeView === 'home' ? '#6366f1' : '#e0e7ff', color: activeView === 'home' ? 'white' : '#3730a3', border: 'none', borderRadius: '8px', padding: '10px 20px', fontWeight: '600', cursor: 'pointer' }}>Home</button>
        {/* Future: Add more navigation buttons here */}
      </nav>
      {activeView === 'home' && (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '80vh' }}>
          <div style={{ background: 'white', borderRadius: '16px', padding: '40px', boxShadow: '0 10px 30px rgba(0,0,0,0.2)', textAlign: 'center', maxWidth: '500px' }}>
            <h1 style={{ fontSize: '32px', fontWeight: '700', marginBottom: '20px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', WebkitBackgroundClip: 'text', WebkitTextFillColor: 'transparent' }}>
              Barcode Management System
            </h1>
            <p style={{ color: '#666', fontSize: '16px', marginBottom: '30px' }}>
              Welcome to your barcode management app.<br />
              Use the navigation above to get started.
            </p>
          </div>
        </div>
      )}
    </div>
  );
}
