import React, { forwardRef } from 'react';

const PrintableBarcode = forwardRef(({ barcode }, ref) => (
  <div
    ref={ref}
    style={{
      width: '4.33in',
      height: '2.56in',
      padding: '0.15in',
      margin: '0',
      backgroundColor: 'white',
      color: 'black',
      fontFamily: 'Arial, sans-serif',
      boxSizing: 'border-box',
      pageBreakAfter: 'always',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    }}
  >
    <style>{`
      @media print {
        @page {
          size: 4.33in 2.56in;
          margin: 0;
        }
        body {
          margin: 0;
          padding: 0;
        }
        html, body {
          height: 2.56in;
          overflow: hidden;
        }
      }
    `}</style>
    
    <div style={{ textAlign: 'center', marginBottom: '0.08in' }}>
      <h3 style={{ margin: '0 0 0.05in 0', fontSize: '14px', fontWeight: 'bold' }}>
        {barcode.productName}
      </h3>
      {barcode.serialNumber && (
        <p style={{ margin: '0', fontSize: '10px', color: '#333', fontWeight: '500' }}>
          SN: {barcode.serialNumber}
        </p>
      )}
    </div>
    
    <div style={{ textAlign: 'center', flex: '1', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
      <svg ref={(el) => {
        if (el) {
          import('jsbarcode').then(({ default: JsBarcode }) => {
            JsBarcode(el, barcode.barcodeValue, {
              format: barcode.barcodeType || 'CODE128',
              displayValue: true,
              fontSize: 12,
              lineColor: '#000',
              width: 2,
              height: 40,
              margin: 0
            });
          });
        }
      }} />
    </div>
    
    <div style={{ marginTop: 'auto' }}>
      {barcode.remark && (
        <p style={{ 
          textAlign: 'center', 
          fontSize: '8px', 
          color: '#666', 
          margin: '0.05in 0', 
          fontStyle: 'italic',
          wordWrap: 'break-word',
          lineHeight: '1.2'
        }}>
          {barcode.remark}
        </p>
      )}
      
      {(barcode.areaManager || barcode.supervisor) && (
        <div style={{ 
          marginTop: '0.05in', 
          fontSize: '7px', 
          color: '#555', 
          borderTop: '1px solid #ccc', 
          paddingTop: '0.03in',
          lineHeight: '1.3',
          display: 'flex',
          justifyContent: 'space-between'
        }}>
          {barcode.areaManager && <div>Mgr: {barcode.areaManager}</div>}
          {barcode.supervisor && <div>Sup: {barcode.supervisor}</div>}
        </div>
      )}
    </div>
  </div>
));

export default PrintableBarcode;
