import React, { forwardRef } from 'react';


const PrintableBarcode = forwardRef(({ barcode }, ref) => {
  // Default values for missing fields
  const productName = barcode.productName || "DELL";
  const serialNumber = barcode.serialNumber || "CND45784147";
  const barcodeValue = barcode.barcodeValue || "CND45784147";
  // Use provided date or fallback to Nov 11, 2025
  let date = barcode.lastUpdate || barcode.date;
  if (!date) {
    date = "Nov 11, 2025";
  }
  const areaManager = barcode.areaManager || "PASIDU";
  const supervisor = barcode.supervisor || "NADUN";

  return (
    <div
      ref={ref}
      style={{
        width: '4.33in',
        height: '2.56in',
        padding: '0.15in',
        margin: '0',
        backgroundColor: 'white',
        color: 'black',
        fontFamily: 'Arial, sans-serif',
        boxSizing: 'border-box',
        pageBreakAfter: 'avoid',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between',
        overflow: 'hidden'
      }}
    >
      <style>{`
        @media print {
          @page {
            size: 4.33in 2.56in;
            margin: 0;
          }
          body {
            margin: 0;
            padding: 0;
          }
          html, body {
            height: 2.56in;
            overflow: hidden;
          }
        }
      `}</style>

      {/* Product Name */}
      <div style={{ textAlign: 'center', marginBottom: '0.08in' }}>
        <h3 style={{ margin: '0 0 0.05in 0', fontSize: '14px', fontWeight: 'bold' }}>{productName}</h3>
        <p style={{ margin: '0', fontSize: '10px', color: '#333', fontWeight: '500' }}>SN: {serialNumber}</p>
      </div>

      {/* Barcode SVG and Value */}
      <div style={{ textAlign: 'center', flex: '1', display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column' }}>
        <svg ref={(el) => {
          if (el) {
            import('jsbarcode').then(({ default: JsBarcode }) => {
              JsBarcode(el, barcodeValue, {
                format: barcode.barcodeType || 'CODE128',
                displayValue: true,
                fontSize: 12,
                lineColor: '#000',
                width: 2,
                height: 40,
                margin: 0
              });
            });
          }
        }} />
        <div style={{ fontSize: '12px', marginTop: '0.05in', fontWeight: 'bold' }}>{barcodeValue}</div>
      </div>

      {/* Date */}
      <div style={{ textAlign: 'center', fontSize: '10px', color: '#059669', margin: '0.03in 0', fontWeight: '600', wordWrap: 'break-word', lineHeight: '1.2' }}>{date}</div>

      {/* Area Manager and Supervisor */}
      <div style={{ marginTop: '0.06in', fontSize: '9px', color: '#000', borderTop: '2px solid #333', paddingTop: '0.05in', lineHeight: '1.6', display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '0.1in', fontWeight: 'bold' }}>
        <div style={{ textAlign: 'left' }}>
          <span style={{ fontSize: '8px', color: '#666', fontWeight: 'normal' }}>Area Manager:</span>
          <br />
          <span style={{ fontSize: '10px', color: '#000', textTransform: 'uppercase' }}>{areaManager}</span>
        </div>
        <div style={{ textAlign: 'right' }}>
          <span style={{ fontSize: '8px', color: '#666', fontWeight: 'normal' }}>Supervisor:</span>
          <br />
          <span style={{ fontSize: '10px', color: '#000', textTransform: 'uppercase' }}>{supervisor}</span>
        </div>
      </div>
    </div>
  );
});

export default PrintableBarcode;
