import React, { useState, useRef } from 'react';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';

export default function App() {
  const [barcodeForm, setBarcodeForm] = useState({
    productName: '',
    serialNumber: '',
    barcodeValue: '',
    lastUpdate: '',
    areaManager: '',
    supervisor: '',
    mobileNumber: '',
    location: '',
    fault: '',
    accepted: '',
    deliveredBy: '',
    remark: ''
  });
  const [scanValue, setScanValue] = useState('');
  const [scannedData, setScannedData] = useState(null);
  const barcodeRef = useRef();

  // Automatically update barcodeValue when serialNumber changes
  const handleSerialChange = (e) => {
    const serial = e.target.value.toUpperCase();
    setBarcodeForm(form => ({
      ...form,
      serialNumber: serial,
      barcodeValue: serial // barcodeValue is always the serial number
    }));
  };

  // Use react-to-print for printing
  const handlePrint = useReactToPrint({
    content: () => barcodeRef.current
  });

  // Handle barcode scan
  const handleScan = async () => {
    if (!scanValue.trim()) return;
    try {
      const { api } = await import('./services/api');
      const res = await api.get(`/api/barcodes?barcodeValue=${scanValue.trim()}`);
      if (res && !res.error && res.data && res.data.length > 0) {
        setScannedData(res.data[0]);
      } else {
        setScannedData(null);
        alert('Barcode not found!');
      }
    } catch (err) {
      setScannedData(null);
      alert('Scan error: ' + err.message);
    }
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #eef2f7 0%, #e0e7ff 100%)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <nav style={{ background: 'white', borderRadius: '0 0 20px 20px', boxShadow: '0 2px 12px rgba(0,0,0,0.10)', padding: '24px 40px', display: 'flex', gap: '32px', alignItems: 'center', marginBottom: '32px', justifyContent: 'center' }}>
        <img src="https://tse1.mm.bing.net/th/id/OIP.6ISiR68fTJizRexWth0ABQAAAA?cb=ucfimg2ucfimg=1&rs=1&pid=ImgDetMain&o=7&rm=3" alt="Logo" style={{ height: '80px', marginRight: '32px', objectFit: 'contain', boxShadow: '0 2px 8px #e0e7ff', background: 'white', borderRadius: '20px', padding: '4px' }} />
        <span style={{ fontSize: '32px', fontWeight: '900', color: '#6366f1', letterSpacing: '2px', marginRight: 'auto' }}>IT Department Asset Management System</span>
        <img src="https://cdn-icons-png.flaticon.com/512/5264/5264565.png" alt="User Icon" style={{ width: '40px', height: '40px', borderRadius: '50%', marginLeft: '24px', boxShadow: '0 2px 8px #e0e7ff', objectFit: 'cover', cursor: 'pointer', position: 'relative' }} />
      </nav>
      {/* Scan Barcode Section */}
      <div style={{ maxWidth: '600px', margin: '0 auto', marginBottom: '32px', background: '#fff', borderRadius: '16px', boxShadow: '0 2px 8px #e0e7ff', padding: '32px' }}>
        <h2 style={{ fontSize: '24px', fontWeight: '700', color: '#2563eb', marginBottom: '16px' }}>Scan Barcode</h2>
        <div style={{ display: 'flex', gap: '12px', marginBottom: '16px' }}>
          <input
            type="text"
            value={scanValue}
            onChange={e => setScanValue(e.target.value)}
            placeholder="Enter or scan barcode value"
            style={{ flex: 1, padding: '12px', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }}
          />
          <button type="button" onClick={handleScan} style={{ padding: '12px 24px', background: '#059669', color: 'white', border: 'none', borderRadius: '8px', fontWeight: '700', fontSize: '18px', cursor: 'pointer' }}>Show</button>
        </div>
        {scannedData && (
          <div style={{ background: '#f9fafb', borderRadius: '12px', boxShadow: '0 2px 8px #e5e7eb', padding: '24px', marginTop: '16px' }}>
            <div style={{ fontWeight: '700', fontSize: '20px', color: '#059669', marginBottom: '8px' }}>{scannedData.productName}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Serial Number:</strong> {scannedData.serialNumber}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px', color: '#000' }}><strong>Area Manager:</strong> {scannedData.areaManager}</div>
            {scannedData.asset !== 'Head Office' && (
              <div style={{ fontSize: '16px', marginBottom: '4px', color: '#000' }}><strong>Supervisor:</strong> {scannedData.supervisor}</div>
            )}
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Mobile Number:</strong> {scannedData.mobileNumber}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Location:</strong> {scannedData.location}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Fault:</strong> {scannedData.fault}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Accepted:</strong> {scannedData.accepted}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Delivered by:</strong> {scannedData.deliveredBy}</div>
            <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Remark:</strong> {scannedData.remark}</div>
            <div style={{ fontSize: '14px', color: '#6b7280', marginTop: '8px' }}><strong>Last Update:</strong> {scannedData.lastUpdate}</div>
          </div>
        )}
      </div>
      {/* Only print preview below */}
      <div style={{ width: '100vw', minHeight: '100vh', display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'linear-gradient(135deg, #e0e7ff 0%, #f3f4f6 100%)' }}>
        <div style={{ background: 'white', borderRadius: '24px', boxShadow: '0 8px 32px rgba(0,0,0,0.10)', padding: '48px 40px', maxWidth: '900px', width: '100%', display: 'flex', flexDirection: 'column', gap: '40px', alignItems: 'center', justifyContent: 'center' }}>
          {/* Removed Create Barcode form and Add to Database button */}
          <div ref={barcodeRef} style={{ background: '#f9fafb', borderRadius: '16px', padding: '32px', boxShadow: '0 4px 15px rgba(0,0,0,0.08)', maxWidth: '400px', width: '100%', minHeight: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
            <PrintableBarcode ref={barcodeRef} barcode={barcodeForm} />
          </div>
        </div>
      </div>
    </div>
  );
}
