import React, { forwardRef } from 'react';

const PrintableBarcode = forwardRef(({ barcode }, ref) => (
  <div
    ref={ref}
    style={{
      padding: '20px',
      width: '300px',
      margin: '0 auto',
      backgroundColor: 'white',
      color: 'black',
      fontFamily: 'Arial, sans-serif'
    }}
  >
    <h3 style={{ textAlign: 'center', marginBottom: '10px' }}>{barcode.productName}</h3>
    {barcode.serialNumber && (
      <p style={{ textAlign: 'center', fontSize: '12px', marginBottom: '5px', color: '#555' }}>
        Serial: {barcode.serialNumber}
      </p>
    )}
    <div style={{ textAlign: 'center' }}>
      <svg ref={(el) => {
        if (el) {
          import('jsbarcode').then(({ default: JsBarcode }) => {
            JsBarcode(el, barcode.barcodeValue, {
              format: barcode.barcodeType || 'CODE128',
              displayValue: true,
              fontSize: 16,
              lineColor: '#000',
              margin: 10
            });
          });
        }
      }} />
    </div>
    <p style={{ textAlign: 'center', marginTop: '10px', fontSize: '14px' }}>
      ID: {barcode.productId}
    </p>
    {barcode.remark && (
      <p style={{ textAlign: 'center', fontSize: '11px', color: '#666', marginTop: '5px', fontStyle: 'italic' }}>
        {barcode.remark}
      </p>
    )}
    {(barcode.areaManager || barcode.supervisor) && (
      <div style={{ marginTop: '10px', fontSize: '10px', color: '#555', borderTop: '1px solid #ddd', paddingTop: '8px' }}>
        {barcode.areaManager && <div>Manager: {barcode.areaManager}</div>}
        {barcode.supervisor && <div>Supervisor: {barcode.supervisor}</div>}
      </div>
    )}
  </div>
));

export default PrintableBarcode;
