import React, { useEffect, useState } from 'react';
import { api } from '../services/api';

const BarcodeList = ({ scannedBarcode, refreshTrigger }) => {
  const [barcodes, setBarcodes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBarcodes = async () => {
      setLoading(true);
      try {
        let res;
        if (scannedBarcode) {
          res = await api.get(`/barcodes?barcodeValue=${scannedBarcode}`);
        } else {
          res = await api.get('/barcodes');
        }
        if (res && !res.error) {
          setBarcodes(res.data || []);
        } else {
          setError(res.error || 'Failed to fetch barcodes');
        }
      } catch (err) {
        setError(err.message);
      }
      setLoading(false);
    };
    fetchBarcodes();
  }, [scannedBarcode, refreshTrigger]);

  // Add this effect to refresh the list when a new barcode is added
  useEffect(() => {
    const handleBarcodeAdded = () => {
      if (!scannedBarcode) {
        api.get('/barcodes').then(res => {
          if (res && !res.error) setBarcodes(res.data || []);
        });
      }
    };
    window.addEventListener('barcodeAdded', handleBarcodeAdded);
    return () => window.removeEventListener('barcodeAdded', handleBarcodeAdded);
  }, [scannedBarcode]);

  if (loading) return <div>Loading barcodes...</div>;
  if (error) return <div style={{ color: 'red' }}>Error: {error}</div>;
  if (!barcodes.length) return <div>No barcodes found.</div>;

  return (
    <div style={{ width: '100%' }}>
      {barcodes.map((barcode, idx) => (
        <div key={barcode._id || idx} style={{ background: '#f9fafb', borderRadius: '12px', boxShadow: '0 2px 8px #e5e7eb', padding: '24px', marginBottom: '24px' }}>
          <div style={{ fontWeight: '700', fontSize: '20px', color: '#059669', marginBottom: '8px' }}>{barcode.productName}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Serial Number:</strong> {barcode.serialNumber}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px', color: '#000' }}><strong>Area Manager:</strong> {barcode.areaManager}</div>
          {barcode.asset !== 'Head Office' && (
            <div style={{ fontSize: '16px', marginBottom: '4px', color: '#000' }}><strong>Supervisor:</strong> {barcode.supervisor}</div>
          )}
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Mobile Number:</strong> {barcode.mobileNumber}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Location:</strong> {barcode.location}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Fault:</strong> {barcode.fault}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Accepted:</strong> {barcode.accepted}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Delivered by:</strong> {barcode.deliveredBy}</div>
          <div style={{ fontSize: '16px', marginBottom: '4px' }}><strong>Remark:</strong> {barcode.remark}</div>
          <div style={{ fontSize: '14px', color: '#6b7280', marginTop: '8px' }}><strong>Last Update:</strong> {barcode.lastUpdate}</div>
        </div>
      ))}
    </div>
  );
};

export default BarcodeList;
