import React, { useState, useRef } from 'react';
import ScannerInput from './components/ScannerInput';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';

export default function App() {
  const [activeView, setActiveView] = useState('scan'); // Default to scan view
  const [barcodeForm, setBarcodeForm] = useState({
    productName: '',
    serialNumber: '',
    barcodeValue: '',
    lastUpdate: '',
    areaManager: '',
    supervisor: ''
  });
  const barcodeRef = useRef();

  // Automatically update barcodeValue when serialNumber changes
  const handleSerialChange = (e) => {
    const serial = e.target.value;
    setBarcodeForm(form => ({
      ...form,
      serialNumber: serial,
      barcodeValue: serial // barcodeValue is always the serial number
    }));
  };

  // Use react-to-print for printing
  const handlePrint = useReactToPrint({
    content: () => barcodeRef.current
  });

  // Placeholder scan handler
  const handleScan = (barcode) => {
    alert(`Scanned: ${barcode}`);
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <nav style={{ background: 'white', borderRadius: '0 0 16px 16px', boxShadow: '0 2px 8px rgba(0,0,0,0.08)', padding: '16px 32px', display: 'flex', gap: '24px', alignItems: 'center' }}>
        <button onClick={() => setActiveView('scan')} style={{ background: activeView === 'scan' ? '#6366f1' : '#e0e7ff', color: activeView === 'scan' ? 'white' : '#3730a3', border: 'none', borderRadius: '8px', padding: '10px 20px', fontWeight: '600', cursor: 'pointer' }}>Scan Barcode</button>
        <button onClick={() => setActiveView('create')} style={{ background: activeView === 'create' ? '#059669' : '#e0e7ff', color: activeView === 'create' ? 'white' : '#059669', border: 'none', borderRadius: '8px', padding: '10px 20px', fontWeight: '600', cursor: 'pointer' }}>Create Barcode</button>
        <button onClick={() => setActiveView('list')} style={{ background: activeView === 'list' ? '#7c3aed' : '#e0e7ff', color: activeView === 'list' ? 'white' : '#7c3aed', border: 'none', borderRadius: '8px', padding: '10px 20px', fontWeight: '600', cursor: 'pointer' }}>All Barcodes</button>
      </nav>
      {activeView === 'scan' && (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '80vh' }}>
          <div style={{ background: 'white', borderRadius: '16px', padding: '40px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', textAlign: 'center', maxWidth: '500px', width: '100%' }}>
            <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '20px', color: '#2563eb' }}>ðŸ“¦ Scan Barcode</h2>
            <ScannerInput onScan={handleScan} />
          </div>
        </div>
      )}
      {activeView === 'create' && (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '80vh', width: '100%' }}>
          <div style={{ background: 'white', borderRadius: '24px', boxShadow: '0 8px 32px rgba(0,0,0,0.12)', padding: '48px 32px', maxWidth: '480px', width: '100%', marginBottom: '32px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
            <h2 style={{ fontSize: '28px', fontWeight: '900', marginBottom: '32px', color: '#059669', letterSpacing: '1px' }}>âž• Create Barcode</h2>
            <form style={{ width: '100%' }}>
              <input type="text" placeholder="Product Name" value={barcodeForm.productName} onChange={e => setBarcodeForm({ ...barcodeForm, productName: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Serial Number" value={barcodeForm.serialNumber} onChange={handleSerialChange} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Area Manager" value={barcodeForm.areaManager} onChange={e => setBarcodeForm({ ...barcodeForm, areaManager: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Supervisor" value={barcodeForm.supervisor} onChange={e => setBarcodeForm({ ...barcodeForm, supervisor: e.target.value })} style={{ marginBottom: '24px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <div style={{ display: 'flex', gap: '16px', marginBottom: '24px' }}>
                <button type="button" style={{ flex: 1, padding: '14px 0', background: '#059669', color: 'white', border: 'none', borderRadius: '8px', fontWeight: '700', fontSize: '16px', cursor: 'pointer' }} onClick={() => setBarcodeForm({ ...barcodeForm, lastUpdate: new Date().toLocaleString() })}>Preview Barcode</button>
                <button type="button" style={{ flex: 1, padding: '14px 0', background: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', fontWeight: '700', fontSize: '16px', cursor: 'pointer' }} onClick={handlePrint}>Print Barcode</button>
              </div>
            </form>
            <div ref={barcodeRef} style={{ background: '#f9fafb', borderRadius: '16px', padding: '32px', boxShadow: '0 4px 15px rgba(0,0,0,0.08)', maxWidth: '400px', width: '100%', minHeight: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
              <PrintableBarcode ref={barcodeRef} barcode={barcodeForm} />
            </div>
          </div>
        </div>
      )}
      {activeView === 'list' && (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '80vh' }}>
          <div style={{ background: 'white', borderRadius: '16px', padding: '40px', boxShadow: '0 4px 15px rgba(0,0,0,0.1)', textAlign: 'center', maxWidth: '700px', width: '100%' }}>
            <h2 style={{ fontSize: '24px', fontWeight: '700', marginBottom: '20px', color: '#7c3aed' }}>ðŸ“„ All Barcodes</h2>
            <p style={{ color: '#666', fontSize: '16px' }}>List of all entered barcodes will be shown here.</p>
          </div>
        </div>
      )}
    </div>
  );
}
