import React, { useState, useEffect, useRef } from 'react';
import { api } from './services/api';
import ScannerInput from './components/ScannerInput';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';
import JsBarcode from 'jsbarcode';

export default function App() {
  const [barcodes, setBarcodes] = useState([]);
  const [form, setForm] = useState({ productName: '', serialNumber: '', barcodeValue: '', remark: '', areaManager: '', supervisor: '' });
  const [scannedItem, setScannedItem] = useState(null);
  const [error, setError] = useState('');

  const componentRef = useRef();
  const previewBarcodeRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  const handlePrintPreview = useReactToPrint({
    content: () => previewBarcodeRef.current,
  });

  // Load all barcodes
  useEffect(() => {
    loadBarcodes();
  }, []);

  // Generate preview barcode
  useEffect(() => {
    const barcodeValue = form.barcodeValue || form.serialNumber;
    if (barcodeValue && previewBarcodeRef.current) {
      const svg = previewBarcodeRef.current.querySelector('svg');
      if (svg) {
        try {
          JsBarcode(svg, barcodeValue, {
            format: 'CODE128',
            displayValue: true,
            fontSize: 16,
            lineColor: '#000',
            margin: 10,
            background: '#fff'
          });
        } catch (err) {
          console.error('Barcode generation error:', err);
        }
      }
    }
  }, [form.serialNumber, form.barcodeValue]);

  const loadBarcodes = async () => {
    try {
      const data = await api.get('/barcodes');
      setBarcodes(data);
    } catch (err) {
      setError('Failed to load barcodes');
    }
  };

  const handleScan = async (value) => {
    setError('');
    try {
      const result = await api.get(`/barcodes/scan?value=${value}`);
      setScannedItem(result);
    } catch (err) {
      setError(err.error || 'Not found');
      setScannedItem(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Generate barcode value from serial number if not provided
      const barcodeData = {
        ...form,
        barcodeValue: form.barcodeValue || form.serialNumber || `BC${Date.now()}`
      };
      await api.post('/barcodes', barcodeData);
      setForm({ productName: '', serialNumber: '', barcodeValue: '', remark: '', areaManager: '', supervisor: '' });
      loadBarcodes();
    } catch (err) {
      setError(err.error || 'Failed to create barcode');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this barcode?')) return;
    try {
      await api.del(`/barcodes/${id}`);
      loadBarcodes();
    } catch (err) {
      setError('Failed to delete');
    }
  };

  return (
    <div style={{ padding: '20px', maxWidth: '800px', margin: '0 auto', color: '#fff', backgroundColor: '#121212', minHeight: '100vh' }}>
      <h1>üì¶ Barcode System</h1>

      {/* Scanner */}
      <section>
        <h2>üîç Scan Barcode</h2>
        <ScannerInput onScan={handleScan} />
        {error && <p style={{ color: 'red' }}>{error}</p>}
        {scannedItem && (
          <div style={{ backgroundColor: '#1e1e1e', padding: '15px', borderRadius: '6px', marginTop: '10px' }}>
            <h3>‚úÖ Found: {scannedItem.productName}</h3>
            {scannedItem.serialNumber && <p>Serial Number: {scannedItem.serialNumber}</p>}
            {scannedItem.remark && <p style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Remark: {scannedItem.remark}</p>}
            {scannedItem.areaManager && <p style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Area Manager: {scannedItem.areaManager}</p>}
            {scannedItem.supervisor && <p style={{ fontSize: '14px', color: '#aaa', marginTop: '5px' }}>Supervisor: {scannedItem.supervisor}</p>}
            <button onClick={handlePrint} style={{ padding: '8px 12px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}>
              Print Label
            </button>
            <div style={{ display: 'none' }}>
              <PrintableBarcode ref={componentRef} barcode={scannedItem} />
            </div>
          </div>
        )}
      </section>

      {/* Create Form */}
      <section style={{ marginTop: '30px' }}>
        <h2>‚ûï Add New Barcode</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="Area Manager"
            value={form.areaManager}
            onChange={(e) => setForm({ ...form, areaManager: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444' }}
            required
          />
          <input
            type="text"
            placeholder="Supervisor"
            value={form.supervisor}
            onChange={(e) => setForm({ ...form, supervisor: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444' }}
            required
          />
          <input
            type="text"
            placeholder="Product Name"
            value={form.productName}
            onChange={(e) => setForm({ ...form, productName: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444' }}
            required
          />
          <input
            type="text"
            placeholder="Serial Number"
            value={form.serialNumber}
            onChange={(e) => {
              const value = e.target.value.toUpperCase().replace(/[^A-Z0-9]/g, '');
              setForm({ ...form, serialNumber: value });
            }}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444', textTransform: 'uppercase' }}
            required
          />
          <input
            type="text"
            placeholder="Barcode Value (auto-generated from serial number)"
            value={form.barcodeValue}
            onChange={(e) => setForm({ ...form, barcodeValue: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444' }}
          />
          <textarea
            placeholder="Remark (optional)"
            value={form.remark}
            onChange={(e) => setForm({ ...form, remark: e.target.value })}
            style={{ width: '100%', padding: '10px', margin: '5px 0', backgroundColor: '#222', color: 'white', border: '1px solid #444', minHeight: '60px', fontFamily: 'inherit' }}
          />
          {(form.serialNumber || form.barcodeValue) && (
            <div style={{ marginTop: '15px', padding: '15px', backgroundColor: '#1e1e1e', border: '1px solid #444', borderRadius: '6px' }}>
              <h4 style={{ marginBottom: '10px', color: '#4CAF50' }}>Barcode Preview:</h4>
              <div ref={previewBarcodeRef} style={{ backgroundColor: 'white', padding: '20px', borderRadius: '4px', textAlign: 'center' }}>
                <svg></svg>
              </div>
              <button
                type="button"
                onClick={handlePrintPreview}
                style={{ marginTop: '10px', padding: '8px 12px', backgroundColor: '#4CAF50', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', width: '100%' }}
              >
                üñ®Ô∏è Print Barcode Preview
              </button>
            </div>
          )}
          <button
            type="submit"
            style={{ padding: '10px 20px', backgroundColor: '#2196F3', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}
          >
            üì§ Upload to Database
          </button>
        </form>
      </section>

      {/* List */}
      <section style={{ marginTop: '30px' }}>
        <h2>üìÑ All Barcodes ({barcodes.length})</h2>
        {barcodes.map((b) => (
          <div key={b._id} style={{ backgroundColor: '#1e1e1e', padding: '12px', margin: '8px 0', borderRadius: '6px' }}>
            <strong>{b.productName}</strong> | {b.barcodeValue}
            {b.serialNumber && <span> | SN: {b.serialNumber}</span>}
            {b.remark && <div style={{ fontSize: '12px', color: '#888', marginTop: '4px' }}>üìù {b.remark}</div>}
            {(b.areaManager || b.supervisor) && (
              <div style={{ fontSize: '12px', color: '#888', marginTop: '4px' }}>
                {b.areaManager && <span>üëî Manager: {b.areaManager}</span>}
                {b.areaManager && b.supervisor && <span> | </span>}
                {b.supervisor && <span>üë∑ Supervisor: {b.supervisor}</span>}
              </div>
            )}
            <div>
              <button
                onClick={() => {
                  setScannedItem(b);
                  setTimeout(handlePrint, 100);
                }}
                style={{ marginRight: '8px', padding: '4px 8px', backgroundColor: '#444', color: 'white', border: 'none', borderRadius: '3px' }}
              >
                Print
              </button>
              <button
                onClick={() => handleDelete(b._id)}
                style={{ padding: '4px 8px', backgroundColor: '#f44336', color: 'white', border: 'none', borderRadius: '3px' }}
              >
                Delete
              </button>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
}
