import React, { useState, useEffect, useRef } from 'react';
import { api } from './services/api';
import ScannerInput from './components/ScannerInput';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';
import JsBarcode from 'jsbarcode';

export default function App() {
  const [barcodes, setBarcodes] = useState([]);
  const [form, setForm] = useState({ productName: '', serialNumber: '', barcodeValue: '', remark: '', areaManager: '', supervisor: '' });
  const [scannedItem, setScannedItem] = useState(null);
  const [error, setError] = useState('');
  const [lastUpdate, setLastUpdate] = useState(null);

  const componentRef = useRef();
  const previewBarcodeRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  const handlePrintPreview = useReactToPrint({
    content: () => previewBarcodeRef.current,
  });

  // Load all barcodes
  useEffect(() => {
    loadBarcodes();
  }, []);

  // Generate preview barcode
  useEffect(() => {
    const barcodeValue = form.barcodeValue || form.serialNumber;
    if (barcodeValue && previewBarcodeRef.current) {
      const svg = previewBarcodeRef.current.querySelector('svg');
      if (svg) {
        try {
          JsBarcode(svg, barcodeValue, {
            format: 'CODE128',
            displayValue: true,
            fontSize: 16,
            lineColor: '#000',
            margin: 10,
            background: '#fff'
          });
        } catch (err) {
          console.error('Barcode generation error:', err);
        }
      }
    }
  }, [form.serialNumber, form.barcodeValue]);

  const loadBarcodes = async () => {
    try {
      const data = await api.get('/barcodes');
      setBarcodes(data);
    } catch (err) {
      setError('Failed to load barcodes');
    }
  };

  const handleScan = async (value) => {
    setError('');
    try {
      const result = await api.get(`/barcodes/scan?value=${value}`);
      setScannedItem(result);
    } catch (err) {
      setError(err.error || 'Not found');
      setScannedItem(null);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Generate barcode value from serial number if not provided
      const barcodeData = {
        ...form,
        barcodeValue: form.barcodeValue || form.serialNumber || `BC${Date.now()}`
      };
      await api.post('/barcodes', barcodeData);
      setForm({ productName: '', serialNumber: '', barcodeValue: '', remark: '', areaManager: '', supervisor: '' });
      loadBarcodes();
    } catch (err) {
      setError(err.error || 'Failed to create barcode');
    }
  };

  const handleDelete = async (id) => {
    if (!window.confirm('Delete this barcode?')) return;
    try {
      await api.del(`/barcodes/${id}`);
      loadBarcodes();
    } catch (err) {
      setError('Failed to delete');
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      padding: '40px 20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif'
    }}>
      <div style={{ 
        maxWidth: '1200px', 
        margin: '0 auto'
      }}>
        {/* Header */}
        <div style={{
          background: 'white',
          borderRadius: '16px',
          padding: '30px',
          marginBottom: '30px',
          boxShadow: '0 10px 30px rgba(0,0,0,0.2)',
          textAlign: 'center'
        }}>
          <h1 style={{ 
            margin: '0',
            fontSize: '36px',
            background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: '700'
          }}>
            üì¶ Barcode Management System
          </h1>
          <p style={{ margin: '10px 0 0 0', color: '#666', fontSize: '14px' }}>
            Professional Barcode Generation & Tracking
          </p>
        </div>

        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(500px, 1fr))',
          gap: '30px'
        }}>
          {/* Left Column */}
          <div>
            {/* Scanner Section */}
            <section style={{
              background: 'white',
              borderRadius: '16px',
              padding: '25px',
              marginBottom: '30px',
              boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
            }}>
              <h2 style={{ 
                margin: '0 0 20px 0',
                fontSize: '20px',
                color: '#333',
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                üîç <span>Scan Barcode</span>
              </h2>
              <ScannerInput onScan={handleScan} />
              {error && (
                <div style={{ 
                  backgroundColor: '#fee', 
                  color: '#c33',
                  padding: '12px',
                  borderRadius: '8px',
                  marginTop: '15px',
                  fontSize: '14px',
                  border: '1px solid #fcc'
                }}>
                  ‚ö†Ô∏è {error}
                </div>
              )}
              {scannedItem && (
                <div style={{ 
                  backgroundColor: '#f0f9ff',
                  padding: '20px',
                  borderRadius: '12px',
                  marginTop: '15px',
                  border: '2px solid #3b82f6'
                }}>
                  <h3 style={{ margin: '0 0 15px 0', color: '#1e40af', fontSize: '18px' }}>
                    ‚úÖ {scannedItem.productName}
                  </h3>
                  <div style={{ fontSize: '14px', color: '#555', lineHeight: '1.8' }}>
                    {scannedItem.serialNumber && (
                      <div><strong>Serial Number:</strong> {scannedItem.serialNumber}</div>
                    )}
                    {scannedItem.areaManager && (
                      <div><strong>Area Manager:</strong> {scannedItem.areaManager}</div>
                    )}
                    {scannedItem.supervisor && (
                      <div><strong>Supervisor:</strong> {scannedItem.supervisor}</div>
                    )}
                    {scannedItem.remark && (
                      <div style={{ marginTop: '10px', fontStyle: 'italic', color: '#666' }}>
                        üí¨ {scannedItem.remark}
                      </div>
                    )}
                  </div>
                  <button 
                    onClick={handlePrint}
                    style={{ 
                      marginTop: '15px',
                      padding: '12px 24px',
                      backgroundColor: '#10b981',
                      color: 'white',
                      border: 'none',
                      borderRadius: '8px',
                      cursor: 'pointer',
                      fontSize: '14px',
                      fontWeight: '600',
                      width: '100%',
                      transition: 'all 0.3s'
                    }}
                    onMouseOver={(e) => e.target.style.backgroundColor = '#059669'}
                    onMouseOut={(e) => e.target.style.backgroundColor = '#10b981'}
                  >
                    üñ®Ô∏è Print Label
                  </button>
                  <div style={{ display: 'none' }}>
                    <PrintableBarcode ref={componentRef} barcode={scannedItem} />
                  </div>
                </div>
              )}
            </section>

            {/* Create Form */}
            <section style={{
              background: 'white',
              borderRadius: '16px',
              padding: '25px',
              boxShadow: '0 4px 15px rgba(0,0,0,0.1)'
            }}>
              <h2 style={{ 
                margin: '0 0 20px 0',
                fontSize: '20px',
                color: '#333',
                display: 'flex',
                alignItems: 'center',
                gap: '10px'
              }}>
                ‚ûï <span>Create New Barcode</span>
              </h2>
              <form onSubmit={handleSubmit}>
                <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '15px' }}>
                  <input
                    type="text"
                    placeholder="üëî Area Manager *"
                    value={form.areaManager}
                    onChange={(e) => setForm({ ...form, areaManager: e.target.value })}
                    style={inputStyle}
                    required
                  />
                  <input
                    type="text"
                    placeholder="üë∑ Supervisor *"
                    value={form.supervisor}
                    onChange={(e) => setForm({ ...form, supervisor: e.target.value })}
                    style={inputStyle}
                    required
                  />
                </div>
                <input
                  type="text"
                  placeholder="üì¶ Product Name *"
                  value={form.productName}
                  onChange={(e) => setForm({ ...form, productName: e.target.value })}
                  style={inputStyle}
                  required
                />
                <input
                  type="text"
                  placeholder="üî¢ Serial Number (A-Z, 0-9 only) *"
                  value={form.serialNumber}
                  onChange={(e) => {
                    const value = e.target.value.toUpperCase().replace(/[^A-Z0-9]/g, '');
                    setForm({ ...form, serialNumber: value });
                  }}
                  style={{ ...inputStyle, textTransform: 'uppercase', fontWeight: '600', letterSpacing: '1px' }}
                  required
                />
                <input
                  type="text"
                  placeholder="üìä Barcode Value (auto-generated)"
                  value={form.barcodeValue}
                  onChange={(e) => setForm({ ...form, barcodeValue: e.target.value })}
                  style={{ ...inputStyle, backgroundColor: '#f9fafb' }}
                />
                <textarea
                  placeholder="üìù Remark (optional)"
                  value={form.remark}
                  onChange={(e) => setForm({ ...form, remark: e.target.value })}
                  style={{ 
                    ...inputStyle,
                    minHeight: '80px',
                    resize: 'vertical',
                    fontFamily: 'inherit'
                  }}
                />
                
                {(form.serialNumber || form.barcodeValue) && (
                  <div style={{ 
                    marginTop: '20px',
                    padding: '20px',
                    backgroundColor: '#f8fafc',
                    border: '2px dashed #cbd5e1',
                    borderRadius: '12px'
                  }}>
                    <h4 style={{ margin: '0 0 15px 0', color: '#10b981', fontSize: '16px', fontWeight: '600' }}>
                      üîç Live Preview
                    </h4>
                    <div ref={previewBarcodeRef} style={{ 
                      backgroundColor: 'white',
                      padding: '20px',
                      borderRadius: '8px',
                      textAlign: 'center',
                      boxShadow: '0 2px 8px rgba(0,0,0,0.1)'
                    }}>
                      <svg></svg>
                    </div>
                    <button
                      type="button"
                      onClick={handlePrintPreview}
                      style={{ 
                        marginTop: '15px',
                        padding: '10px 20px',
                        backgroundColor: '#6366f1',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        cursor: 'pointer',
                        width: '100%',
                        fontSize: '14px',
                        fontWeight: '600',
                        transition: 'all 0.3s'
                      }}
                      onMouseOver={(e) => e.target.style.backgroundColor = '#4f46e5'}
                      onMouseOut={(e) => e.target.style.backgroundColor = '#6366f1'}
                    >
                      üñ®Ô∏è Print Preview
                    </button>
                  </div>
                )}
                
                <button
                  type="submit"
                  style={{ 
                    marginTop: '20px',
                    padding: '14px 28px',
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    cursor: 'pointer',
                    width: '100%',
                    fontSize: '16px',
                    fontWeight: '700',
                    boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
                    transition: 'all 0.3s'
                  }}
                  onMouseOver={(e) => e.target.style.transform = 'translateY(-2px)'}
                  onMouseOut={(e) => e.target.style.transform = 'translateY(0)'}
                >
                  üì§ Upload to Database
                </button>
              </form>
            </section>
          </div>

          {/* Right Column - List */}
          <div>
            <section style={{
              background: 'white',
              borderRadius: '16px',
              padding: '25px',
              boxShadow: '0 4px 15px rgba(0,0,0,0.1)',
              maxHeight: '900px',
              overflowY: 'auto'
            }}>
              <h2 style={{ 
                margin: '0 0 20px 0',
                fontSize: '20px',
                color: '#333',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'space-between'
              }}>
                <span style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                  üìÑ <span>All Barcodes</span>
                </span>
                <span style={{ 
                  fontSize: '14px',
                  backgroundColor: '#667eea',
                  color: 'white',
                  padding: '4px 12px',
                  borderRadius: '20px',
                  fontWeight: '600'
                }}>
                  {barcodes.length}
                </span>
              </h2>
              
              {barcodes.length === 0 ? (
                <div style={{ 
                  textAlign: 'center',
                  padding: '40px 20px',
                  color: '#999'
                }}>
                  <div style={{ fontSize: '48px', marginBottom: '15px' }}>üì≠</div>
                  <p>No barcodes yet. Create your first one!</p>
                </div>
              ) : (
                barcodes.map((b) => (
                  <div 
                    key={b._id}
                    style={{ 
                      backgroundColor: '#f8fafc',
                      padding: '18px',
                      margin: '12px 0',
                      borderRadius: '12px',
                      border: '1px solid #e2e8f0',
                      transition: 'all 0.3s'
                    }}
                    onMouseOver={(e) => {
                      e.currentTarget.style.boxShadow = '0 4px 12px rgba(0,0,0,0.1)';
                      e.currentTarget.style.transform = 'translateY(-2px)';
                    }}
                    onMouseOut={(e) => {
                      e.currentTarget.style.boxShadow = 'none';
                      e.currentTarget.style.transform = 'translateY(0)';
                    }}
                  >
                    <div style={{ marginBottom: '10px' }}>
                      <strong style={{ fontSize: '16px', color: '#1e293b' }}>
                        {b.productName}
                      </strong>
                      <div style={{ 
                        fontSize: '13px',
                        color: '#64748b',
                        marginTop: '4px',
                        fontFamily: 'monospace',
                        backgroundColor: '#fff',
                        padding: '4px 8px',
                        borderRadius: '4px',
                        display: 'inline-block'
                      }}>
                        {b.barcodeValue}
                      </div>
                    </div>
                    
                    {b.serialNumber && (
                      <div style={{ fontSize: '13px', color: '#475569', marginTop: '6px' }}>
                        <strong>SN:</strong> {b.serialNumber}
                      </div>
                    )}
                    
                    {(b.areaManager || b.supervisor) && (
                      <div style={{ 
                        fontSize: '12px',
                        color: '#64748b',
                        marginTop: '8px',
                        paddingTop: '8px',
                        borderTop: '1px solid #e2e8f0'
                      }}>
                        {b.areaManager && <div>üëî {b.areaManager}</div>}
                        {b.supervisor && <div>üë∑ {b.supervisor}</div>}
                      </div>
                    )}
                    
                    {b.remark && (
                      <div style={{ 
                        fontSize: '12px',
                        color: '#64748b',
                        marginTop: '8px',
                        fontStyle: 'italic',
                        backgroundColor: '#fff',
                        padding: '8px',
                        borderRadius: '6px',
                        borderLeft: '3px solid #cbd5e1'
                      }}>
                        üí¨ {b.remark}
                      </div>
                    )}
                    
                    <div style={{ marginTop: '12px', display: 'flex', gap: '8px' }}>
                      <button
                        onClick={() => {
                          setScannedItem(b);
                          setTimeout(handlePrint, 100);
                        }}
                        style={{ 
                          flex: 1,
                          padding: '8px 16px',
                          backgroundColor: '#10b981',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '13px',
                          fontWeight: '600',
                          transition: 'all 0.3s'
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = '#059669'}
                        onMouseOut={(e) => e.target.style.backgroundColor = '#10b981'}
                      >
                        üñ®Ô∏è Print
                      </button>
                      <button
                        onClick={() => handleDelete(b._id)}
                        style={{ 
                          padding: '8px 16px',
                          backgroundColor: '#ef4444',
                          color: 'white',
                          border: 'none',
                          borderRadius: '6px',
                          cursor: 'pointer',
                          fontSize: '13px',
                          fontWeight: '600',
                          transition: 'all 0.3s'
                        }}
                        onMouseOver={(e) => e.target.style.backgroundColor = '#dc2626'}
                        onMouseOut={(e) => e.target.style.backgroundColor = '#ef4444'}
                      >
                        üóëÔ∏è
                      </button>
                    </div>
                  </div>
                ))
              )}
            </section>
          </div>
        </div>
      </div>
    </div>
  );
}

const inputStyle = {
  width: '100%',
  padding: '12px 16px',
  margin: '8px 0',
  backgroundColor: 'white',
  color: '#1e293b',
  border: '2px solid #e2e8f0',
  borderRadius: '8px',
  fontSize: '14px',
  transition: 'all 0.3s',
  boxSizing: 'border-box',
  outline: 'none'
};
