import React, { useState, useRef } from 'react';
import ScannerInput from './components/ScannerInput';
import PrintableBarcode from './components/PrintableBarcode';
import { useReactToPrint } from 'react-to-print';

export default function App() {
  const [activeView, setActiveView] = useState('scan'); // Default to scan view
  const [barcodeForm, setBarcodeForm] = useState({
  productName: '',
  serialNumber: '',
  barcodeValue: '',
  lastUpdate: '',
  areaManager: '',
  supervisor: '',
  mobileNumber: '',
  location: '',
  fault: '',
  accepted: '',
  deliveredBy: '',
  remark: ''
  });
  const barcodeRef = useRef();

  // Automatically update barcodeValue when serialNumber changes
  const handleSerialChange = (e) => {
    const serial = e.target.value.toUpperCase();
    setBarcodeForm(form => ({
      ...form,
      serialNumber: serial,
      barcodeValue: serial // barcodeValue is always the serial number
    }));
  };

  // Use react-to-print for printing
  const handlePrint = useReactToPrint({
    content: () => barcodeRef.current
  });

  // Placeholder scan handler
  const handleScan = (barcode) => {
    alert(`Scanned: ${barcode}`);
  };

  return (
    <div style={{ minHeight: '100vh', background: 'linear-gradient(135deg, #eef2f7 0%, #e0e7ff 100%)', fontFamily: 'Segoe UI, Arial, sans-serif' }}>
      <nav style={{ background: 'white', borderRadius: '0 0 20px 20px', boxShadow: '0 2px 12px rgba(0,0,0,0.10)', padding: '24px 40px', display: 'flex', gap: '32px', alignItems: 'center', marginBottom: '32px' }}>
        <span style={{ fontSize: '32px', fontWeight: '900', color: '#6366f1', letterSpacing: '2px', marginRight: 'auto' }}>Barcode Dashboard</span>
        <button onClick={() => setActiveView('scan')} style={{ background: activeView === 'scan' ? '#6366f1' : '#e0e7ff', color: activeView === 'scan' ? 'white' : '#3730a3', border: 'none', borderRadius: '10px', padding: '14px 28px', fontWeight: '700', fontSize: '18px', cursor: 'pointer', boxShadow: activeView === 'scan' ? '0 2px 8px #6366f133' : 'none' }}>Scan Barcode</button>
        <button onClick={() => setActiveView('create')} style={{ background: activeView === 'create' ? '#059669' : '#e0e7ff', color: activeView === 'create' ? 'white' : '#059669', border: 'none', borderRadius: '10px', padding: '14px 28px', fontWeight: '700', fontSize: '18px', cursor: 'pointer', boxShadow: activeView === 'create' ? '0 2px 8px #05966933' : 'none' }}>Create Barcode</button>
        <button onClick={() => setActiveView('list')} style={{ background: activeView === 'list' ? '#7c3aed' : '#e0e7ff', color: activeView === 'list' ? 'white' : '#7c3aed', border: 'none', borderRadius: '10px', padding: '14px 28px', fontWeight: '700', fontSize: '18px', cursor: 'pointer', boxShadow: activeView === 'list' ? '0 2px 8px #7c3aed33' : 'none' }}>All Barcodes</button>
      </nav>
      {activeView === 'scan' && (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '70vh', width: '100%' }}>
          <div style={{ background: 'white', borderRadius: '20px', padding: '48px', boxShadow: '0 8px 32px rgba(0,0,0,0.10)', textAlign: 'center', maxWidth: '520px', width: '100%' }}>
            <h2 style={{ fontSize: '28px', fontWeight: '900', marginBottom: '32px', color: '#2563eb', letterSpacing: '1px' }}>ðŸ“¦ Scan Barcode</h2>
            <ScannerInput onScan={handleScan} />
          </div>
        </div>
      )}
      {activeView === 'create' && (
        <div style={{ width: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', marginTop: '16px' }}>
          <div style={{ background: 'white', borderRadius: '20px', boxShadow: '0 8px 32px rgba(0,0,0,0.10)', padding: '40px 32px', maxWidth: '900px', width: '100%', display: 'flex', gap: '40px', alignItems: 'flex-start', justifyContent: 'center' }}>
            <form style={{ background: 'white', borderRadius: '16px', padding: '0', boxShadow: 'none', maxWidth: '400px', width: '100%' }}>
              <h2 style={{ fontSize: '28px', fontWeight: '900', marginBottom: '32px', color: '#059669', letterSpacing: '1px' }}>âž• Create Barcode</h2>
              <input type="text" placeholder="Product Name" value={barcodeForm.productName} onChange={e => setBarcodeForm({ ...barcodeForm, productName: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Serial Number" value={barcodeForm.serialNumber} onChange={handleSerialChange} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Mobile Number" value={barcodeForm.mobileNumber} onChange={e => setBarcodeForm({ ...barcodeForm, mobileNumber: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Location" value={barcodeForm.location} onChange={e => setBarcodeForm({ ...barcodeForm, location: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Fault" value={barcodeForm.fault} onChange={e => setBarcodeForm({ ...barcodeForm, fault: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Accepted" value={barcodeForm.accepted} onChange={e => setBarcodeForm({ ...barcodeForm, accepted: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Delivered by" value={barcodeForm.deliveredBy} onChange={e => setBarcodeForm({ ...barcodeForm, deliveredBy: e.target.value })} style={{ marginBottom: '24px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Remark" value={barcodeForm.remark} onChange={e => setBarcodeForm({ ...barcodeForm, remark: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Area Manager" value={barcodeForm.areaManager} onChange={e => setBarcodeForm({ ...barcodeForm, areaManager: e.target.value })} style={{ marginBottom: '16px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <input type="text" placeholder="Supervisor" value={barcodeForm.supervisor} onChange={e => setBarcodeForm({ ...barcodeForm, supervisor: e.target.value })} style={{ marginBottom: '24px', padding: '12px', width: '100%', fontSize: '18px', borderRadius: '8px', border: '1px solid #ddd' }} />
              <div style={{ display: 'flex', gap: '16px', marginBottom: '24px' }}>
                <button
                  type="button"
                  style={{ flex: 1, padding: '14px 0', background: '#059669', color: 'white', border: 'none', borderRadius: '8px', fontWeight: '700', fontSize: '16px', cursor: 'pointer' }}
                  onClick={async () => {
                    try {
                      const { api } = await import('./services/api');
                      const res = await api.post('/barcodes', { ...barcodeForm, lastUpdate: new Date().toLocaleString() });
                      if (res && !res.error) {
                        alert('Data added to database!');
                        setBarcodeForm({
                          productName: '',
                          serialNumber: '',
                          barcodeValue: '',
                          lastUpdate: '',
                          areaManager: '',
                          supervisor: '',
                          mobileNumber: '',
                          location: '',
                          fault: '',
                          accepted: '',
                          deliveredBy: '',
                          remark: ''
                        });
                      } else {
                        alert('Add failed: ' + (res.error || 'Unknown error'));
                      }
                    } catch (err) {
                      alert('Add error: ' + err.message);
                    }
                  }}
                >
                  Add to Database
                </button>
                <button type="button" style={{ flex: 1, padding: '14px 0', background: '#2563eb', color: 'white', border: 'none', borderRadius: '8px', fontWeight: '700', fontSize: '16px', cursor: 'pointer' }} onClick={handlePrint}>Print Barcode</button>
              </div>
            </form>
            <div ref={barcodeRef} style={{ background: '#f9fafb', borderRadius: '16px', padding: '32px', boxShadow: '0 4px 15px rgba(0,0,0,0.08)', maxWidth: '400px', width: '100%', minHeight: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', marginTop: '8px' }}>
              <PrintableBarcode ref={barcodeRef} barcode={barcodeForm} />
            </div>
          </div>
        </div>
      )}
      {activeView === 'list' && (
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', minHeight: '70vh', width: '100%' }}>
          <div style={{ background: 'white', borderRadius: '20px', padding: '48px', boxShadow: '0 8px 32px rgba(0,0,0,0.10)', textAlign: 'center', maxWidth: '800px', width: '100%' }}>
            <h2 style={{ fontSize: '28px', fontWeight: '900', marginBottom: '32px', color: '#7c3aed', letterSpacing: '1px' }}>ðŸ“„ All Barcodes</h2>
            <p style={{ color: '#666', fontSize: '18px', fontWeight: '500', marginBottom: '16px' }}>List of all entered barcodes will be shown here.</p>
            {/* TODO: Barcode list table or cards can be added here */}
          </div>
        </div>
      )}
    </div>
  );
}
