import React, { forwardRef } from 'react';

const PrintableBarcode = forwardRef(({ barcode }, ref) => (
  <div
    ref={ref}
    style={{
      width: '80mm',
      height: 'auto',
      padding: '10mm',
      margin: '0',
      backgroundColor: 'white',
      color: 'black',
      fontFamily: 'Arial, sans-serif',
      boxSizing: 'border-box',
      pageBreakAfter: 'always'
    }}
  >
    <style>{`
      @media print {
        @page {
          size: 80mm auto;
          margin: 0;
        }
        body {
          margin: 0;
          padding: 0;
        }
      }
    `}</style>
    
    <div style={{ textAlign: 'center', marginBottom: '8px' }}>
      <h3 style={{ margin: '0 0 5px 0', fontSize: '16px', fontWeight: 'bold' }}>
        {barcode.productName}
      </h3>
      {barcode.serialNumber && (
        <p style={{ margin: '0', fontSize: '11px', color: '#333' }}>
          SN: {barcode.serialNumber}
        </p>
      )}
    </div>
    
    <div style={{ textAlign: 'center', margin: '10px 0' }}>
      <svg ref={(el) => {
        if (el) {
          import('jsbarcode').then(({ default: JsBarcode }) => {
            JsBarcode(el, barcode.barcodeValue, {
              format: barcode.barcodeType || 'CODE128',
              displayValue: true,
              fontSize: 14,
              lineColor: '#000',
              width: 2,
              height: 60,
              margin: 5
            });
          });
        }
      }} />
    </div>
    
    {barcode.remark && (
      <p style={{ 
        textAlign: 'center', 
        fontSize: '9px', 
        color: '#666', 
        margin: '8px 0 0 0', 
        fontStyle: 'italic',
        wordWrap: 'break-word'
      }}>
        {barcode.remark}
      </p>
    )}
    
    {(barcode.areaManager || barcode.supervisor) && (
      <div style={{ 
        marginTop: '8px', 
        fontSize: '8px', 
        color: '#555', 
        borderTop: '1px solid #ccc', 
        paddingTop: '5px',
        lineHeight: '1.3'
      }}>
        {barcode.areaManager && <div>Manager: {barcode.areaManager}</div>}
        {barcode.supervisor && <div>Supervisor: {barcode.supervisor}</div>}
      </div>
    )}
  </div>
));

export default PrintableBarcode;
